class banner extends Basebanner
{
    public function preSave($event)   {
    
        $this->clearCache($event);
    }
        
    public function preDelete($event)
    {    	
        $this->clearCache($event);
    	unlink( imageHelper::getInstance()->getPath('banner_imagen', $this) );
    }
    
    public function clearCache($event)
    {
        cacheHelper::getInstance()->delete('banner_homeBanner');
    }
			
    public function getImageFilename()
    {
        return $this->getIdBanner() . '.jpg';		
    }
    
    public function getSwfFilename()
    {
        return $this->getIdBanner() . '.swf';		
    }
	    
    public function canOrderDown()
    {
        $first = BannerTable::getInstance()->getFirst();
    
        if ( $first )
        {
            return $first->getIdBanner() != $this->getIdBanner();
        }
    }
    
    public function canOrderUp()
    {
        $last = BannerTable::getInstance()->getLast();
    
        if ( $last )
        {
            return $last->getIdBanner() != $this->getIdBanner();
        }
    }	
}
