class bannerPrincipal extends BasebannerPrincipal
{   
    
    public function preSave($event)
    {
        $this->clearCache($event);
    }
    
    public function preDelete($event)
    {
        // Si el banner no esta activo se elimina su orden
        if ( $this->getActivo() ) {
            $this->setOrden( null );
        }

        $this->clearCache($event);
    }
    
    public function clearCache($event)
    {
        cacheHelper::getInstance()->deleteByPrefix('bannerPrincipal_listVigentes');    
    }
    
    public function getImageFilename()
    {
        return $this->getIdBannerPrincipal() . '.jpg';
    }
    
}
