class carritoProductoItem extends BasecarritoProductoItem
{
	public function getSubTotal()
	{
		return $this->getProductoItem()->getProducto()->getPrecioDeluxe() * $this->getCantidad();
	}
	
	public function postSave($event)
	{
		$session = sessionTable::getInstance()->getSession();
		$session->updateFechaUltimaAccion();
	}
	
	public function postDelete($event)
	{
		$session = sessionTable::getInstance()->getSession();
