class eshopHomeMultimedia extends BaseeshopHomeMultimedia
{
    public function preDelete($event)
    {    	
    	@unlink( imageHelper::getInstance()->getPath('eshop_home_generico', $this) );
        @unlink( imageHelper::getInstance()->getPath('eshop_home_generico', $this, 'hover') );
        @unlink( imageHelper::getInstance()->getPath('eshop_home_generico', $this, 'poster') );
        @unlink( imageHelper::getInstance()->getPath('eshop_home_generico', $this, 'mobile') );
        @unlink( imageHelper::getInstance()->getPath('eshop_home_generico', $this, 'mobile_poster') );

        @unlink( str_replace('.jpg', '.mp4', imageHelper::getInstance()->getPath('eshop_home_generico', $this) ) );
        @unlink( str_replace('.jpg', '_mobile.mp4', imageHelper::getInstance()->getPath('eshop_home_generico', $this) ) );
        @unlink( str_replace('.jpg', '.gif', imageHelper::getInstance()->getPath('eshop_home_generico', $this) ) );
        @unlink( str_replace('.jpg', '_mobile.gif', imageHelper::getInstance()->getPath('eshop_home_generico', $this) ) );
    }

    public function getImageFilename()
    {
        return $this->getIdEshopHomeMultimedia() . '.jpg';
    }

    public function getVideoFilename()
    {
        return $this->getIdEshopHomeMultimedia() . '.mp4';
    }

}
