<?php

/**
 * eshopHomeMultimediaTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class eshopHomeMultimediaTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object eshopHomeMultimediaTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('eshopHomeMultimedia');
    }

    /**
     * Retorna el listado de multimedia para un idEshopHome
     *
     * @param integer $idEshopHome
     *
     * @return Doctrine_Collection
     */
    public function getList($idEshopHome, $indice = null)
    {
        $q = $this->createQuery('ehm')
                  ->addWhere('ehm.id_eshop_home = ?', $idEshopHome);

        if ( $indice ) {
            $q->addWhere('ehm.indice = ?', $indice);
        }

        $q->orderBy('ehm.indice ASC');

        return $q->execute();
    }
}