        return $this->getIdEshopImagenCampaign() . '.jpg';
    }

    public function canOrderDown()
    {
        $first = eshopImagenCampaignTable::getInstance()->getFirst( $this->getIdEshop() );
    
        if ( $first )
        {
            return $first->getIdEshopImagenCampaign() != $this->getIdEshopImagenCampaign();
        }
    }
    
    public function canOrderUp()
    {
        $last = eshopImagenCampaignTable::getInstance()->getLast( $this->getIdEshop() );
    
        if ( $last )
        {
            return $last->getIdEshopImagenCampaign() != $this->getIdEshopImagenCampaign();
        }
    }

