class eshopLookbook extends BaseeshopLookbook
{
    CONST NO_USA   = 'NOUSA';
    const SIN_ZOOM = 'SZOOM';
    const CON_ZOOM = 'CZOOM';

    public function preSave($event)
    {
        $this->clearCache($event);
    }
    
    public function preDelete($event)
    {
        @unlink( imageHelper::getInstance()->getPath('eshop_lookbook', $this) );
        $eshopLookbookProductos = eshopLookbookProductoTable::getInstance()->listByIdEshopLookbook( $this->getIdEshopLookbook() );
        foreach ($eshopLookbookProductos as $eshopLookbookProducto) $eshopLookbookProducto->delete();

        $this->clearCache($event);
    }
    
    public function clearCache($event)
    {
        cacheHelper::getInstance()->delete('eshopLookbook_list_' . $this->getIdEshop() );
    }
    
    public function getImageFilename()
    {
        return $this->getIdEshopLookbook() . '.jpg';
    }

    public function canOrderDown()
    {
        $first = eshopLookbookTable::getInstance()->getFirst( $this->getIdEshop() );
    
        if ( $first )
        {
            return $first->getIdEshopLookbook() != $this->getIdEshopLookbook();
        }
    }
    
    public function canOrderUp()
    {
        $last = eshopLookbookTable::getInstance()->getLast( $this->getIdEshop() );
    
        if ( $last )
        {
            return $last->getIdEshopLookbook() != $this->getIdEshopLookbook();
        }
    }
}

