<?php

/**
 * eshopLookbookTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class eshopLookbookTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object eshopLookbookTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('eshopLookbook');
    }

    /**
     * Retorna el listado ordenado para armar la seccion lookbook
     *
     * @param integer $idEshop
     *
     * @return Doctrine_Collection
     */
    public function listLookbook($idEshop)
    {
        return $this->createQuery('el')
                    ->select('el.*, elp.*')
                    ->leftJoin('el.eshopLookbookProducto elp')
                    ->addWhere('el.id_eshop = ?', $idEshop)
                    ->addWhere('el.activo = ?', true)
                    ->orderBy('el.orden DESC')
                    ->useResultCache(true, 3600, cacheHelper::getInstance()->genKey("eshopLookbook_list_" . $idEshop) )
                    ->execute();
    }

    /**
     * Retorna el eshopLookbook con orden mas bajo
     *
     * @param integer $idEshop
     *
     * @return eshopLookbook
     */
    public function getFirst($idEshop)
    {
        return $this->createQuery('el')
                    ->addWhere('el.id_eshop = ?', $idEshop)
                    ->orderBy('el.orden ASC')
                    ->limit(1)
                    ->fetchOne();
    }
    
    /**
     * Retorna el eshopLookbook con orden mas alto
     *
     * @param integer $idEshop
     *
     * @return eshopLookbook
     */
    public function getLast($idEshop)
    {
        return $this->createQuery('el')
                    ->addWhere('el.id_eshop = ?', $idEshop)
                    ->orderBy('el.orden DESC')
                    ->limit(1)
                    ->fetchOne();
    }
    
    /**
     * Retorna el anterior eshopLookbook para un $orden
     *
     * @param integer $idEshop
     * @param integer $orden
     *
     * @return eshopLookbook
     */
    public function getPrev( $idEshop, $orden )
    {
        return $this->createQuery('el')
                    ->addWhere('el.id_eshop = ?', $idEshop)
                    ->addWhere('el.orden < ?', array( $orden ) )
                    ->orderBy('el.orden DESC')
                    ->limit(1)
                    ->fetchOne();
    }
    
    /**
     * Retorna el siguiente eshopLookbook para un $orden
     *
     * @param integer $idEshop
     * @param integer $orden
     *
     * @return eshopLookbook
     */
    public function getNext( $idEshop, $orden )
    {
        return $this->createQuery('el')
                    ->addWhere('el.id_eshop = ?', $idEshop)
                    ->addWhere('el.orden > ?', array( $orden ) )
                    ->orderBy('el.orden ASC')
                    ->limit(1)
                    ->fetchOne();
    }
}