class faq extends Basefaq
{
    public function preSave($event)   {
        $this->clearCache($event);
    }
       
    public function preDelete($event)
    {
        $this->clearCache($event);
        imageHelper::getInstance()->processDeleteFile('faq_imagen', $this, true );
    }
    
    public function clearCache($event)
    {
        cacheHelper::getInstance()->deleteByPrefix('faqCategoria');
    }
    
    
    public function getImageFilename()
    {
        return $this->getIdFaq() . '.jpg';
    }

    public function canOrderUp()
    {
        $first = faqTable::getInstance()->getFirst( $this->getIdFaqCategoria() );
    
        if ( $first )
        {
            return $first->getIdFaq() != $this->getIdFaq();
        }
    }
    
    public function canOrderDown()
    {
        $last = faqTable::getInstance()->getLast( $this->getIdFaqCategoria() );
    
        if ( $last )
        {
            return $last->getIdFaq() != $this->getIdFaq();
        }
    }
}