class faqCategoria extends BasefaqCategoria
{
    public function __toString()
    {
        return $this->getDenominacion();
    }
    
    public function clearCache($event)
    {
        cacheHelper::getInstance()->deleteByPrefix('faqCategoria');
    }
    
    public function preDelete($event)
    {
        faqTable::getInstance()->deleteByIdFaqCategoria( $this->getIdFaqCategoria() );
        $this->clearCache($event);
    }
    
    public function preSave($event)
    {
        $this->clearCache($event);
    }
    
    public function canOrderUp()
    {
        $first = faqCategoriaTable::getInstance()->getFirst( $this->getIdEshop() );

        
        if ( $first )
        {
            return $first->getIdFaqCategoria() != $this->getIdFaqCategoria();
        }
    }
    
    public function canOrderDown()
    {
        $last = faqCategoriaTable::getInstance()->getLast($this->getIdEshop());
    
        if ( $last )
        {
            return $last->getIdFaqCategoria() != $this->getIdFaqCategoria();
        }
    }
}