class marca extends Basemarca
{
	CONST COND_FISCAL_MONO = 'MON';
	CONST COND_FISCAL_RI = 'RIN';
		
	static $condicionesFiscales = array('' => '', 'MON' => 'Monotributista', 'RIN' => 'Responsable Inscripto');
	
	public function preSave($event)   {
	    
	    $filename = imageHelper::getInstance()->getPath('marca_imagen');
	    
	    if (is_file($filename)) {
	        unlink( $filename );
	    }
	    
	    $this->clearCache($event);
	}
		
	public function preDelete($event) {
	    $this->clearCache($event);
	}
	
	public function clearCache($event)
	{
	    cacheHelper::getInstance()->delete('marca_getOneById_' . $this->getIdMarca());
	}
	
	public function __toString()
	{
		return $this->getNombre();
	}
	
	public function getImageFilename()
	{
		return $this->getIdMarca() . '.jpg';		
	}
    
    public function getCondicionFiscalDenominacion()
    {
    	return self::$condicionesFiscales[$this->getCondicionFiscal()];
    }

}
