class ncredito extends Basencredito
{
    public function getUsuario()
    {
        $nCreditoFacturas = $this->getNcreditoFactura();
        return $nCreditoFacturas[0]->getFactura()->getPedido()->getUsuario();
    }
    
    public function getPedido()
    {
        $nCreditoFacturas = $this->getNcreditoFactura();
        return $nCreditoFacturas[0]->getFactura()->getPedido();
    }
    
    public function getFormatedCAEVencimiento()
    {
        $vencimiento = $this->getCAEVencimiento();
    
        $d = substr($vencimiento, 6, 2);
        $m = substr($vencimiento, 4, 2);
        $y = substr($vencimiento, 0, 4);
    
        return "$d/$m/$y";
    }
    
    public function getFechaEmision($format = 'd/m/Y')
    {
        $ultimoWS = ncreditoWsRequestTable::getInstance()->getLastByIdNCredito( $this->getIdNcredito() );
        return date($format, strtotime( $ultimoWS->getFecha() ));
    }
}