class newsletter extends Basenewsletter
{    
    
	public function getSexoDenominacion()
	{
		return newsletterTable::getInstance()->nombreSexo($this->getSexo());
	}
	    
	public function suscribir()
	{
		// Solo se envia a Remarkety a los suscriptos de DeluxeBuys
		if ( !$this->getIdEshop() ) {
			Remarkety::getInstance()->newSuscriber( $this );	
		}

        $this->save();
	}
}
