<?php

/**
 * notificacionBackendTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class notificacionBackendTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object notificacionBackendTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('notificacionBackend');
    }
    
    public function retrieveBackendList(Doctrine_Query $q)
    {
        $idUsuario = sfContext::getInstance()->getUser()->getGuardUser()->getId();
        
        $rootAlias = $q->getRootAlias();
        $q->addWhere($rootAlias . '.visto = ?', false);
        $q->addWhere($rootAlias . '.id_usuario = ?', $idUsuario);
        return $q;
    }   

    
    /**
     * Retorna la cantidad de notificaciones no atendidas
     *
     * @return int
     */
    public function count()
    {
        $idUsuario = sfContext::getInstance()->getUser()->getGuardUser()->getId();
        
        return $this->createQuery('nb')
                    ->addWhere('nb.visto = ?', false)
                    ->addWhere('nb.id_usuario = ?', $idUsuario)
                    ->count();
    }
    
}