<?php

/**
 * recuperoCarritoTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class recuperoCarritoTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object recuperoCarritoTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('recuperoCarrito');
    }    
    
    /**
     * Retorna un recuperoCarrtio junto con su pedido asociado a partir de su $hash
     *
     * @param integer $hash
     *
     * @return recuperoCarrito
     */
    public function getByHash($hash)
    {
        return $this->createQuery('rc')
                    ->select('rc.*, p.*, ppi.*, pr.*, pi.*')
                    ->innerJoin('rc.pedido p')
                    ->innerJoin('p.pedidoProductoItem ppi')
                    ->innerJoin('ppi.productoItem pi')
                    ->innerJoin('pi.producto pr')
                    ->addWhere('rc.hash = ?', $hash)
                    ->fetchOne();
    }
    
}